import noteContext from "./noteContext";
import { useState } from "react";

const NoteState = (props) => {
  const [DBerror, setDBerror] = useState(false); //to check if db is connected

  const host = "http://127.0.0.1:5000";

  // Getting All Notes
  const [notes, setNotes] = useState([]);

  const fetchAllNotes = async () => {
    try {
      const response = await fetch(`${host}/api/notes/fetchallnotes`, {
        method: "GET",
        headers: {
          "Content-Type": "application/json",
          "auth-token":
            "eyJhbGciOiJIUzI1NiIsInR5cCI6IkpXVCJ9.eyJ1c2VyIjp7ImlkIjoiNjRhOTQ4NGY1MmI5ZDVjZDRjYjlkNmVlIn0sImlhdCI6MTY4OTA2MTkyN30.coa6qVmo4RN2nIuDrSvjW_drJLqfF65XXzxENL4Rwe8",
        },
      });
      const allNotes = await response.json();
      setNotes(allNotes);
    } catch (error) {
      setDBerror(true);
      console.log("Server is Down! Retry Later");
    }
  };

  //AddNote
  const addNote = async (title, description, tag) => {
    //adding notes to DB
    await fetch(`${host}/api/notes/addnotes`, {
      method: "POST",
      headers: {
        "Content-Type": "application/json",
        "auth-token":
          "eyJhbGciOiJIUzI1NiIsInR5cCI6IkpXVCJ9.eyJ1c2VyIjp7ImlkIjoiNjRhOTQ4NGY1MmI5ZDVjZDRjYjlkNmVlIn0sImlhdCI6MTY4OTA2MTkyN30.coa6qVmo4RN2nIuDrSvjW_drJLqfF65XXzxENL4Rwe8",
      },
      body: JSON.stringify({ title, description, tag }),
    });
    showAlert("Notes Added Succesfully","success");
    fetchAllNotes();
  };

  //DeleteNote
  const deleteNote = async (id) => {
    await fetch(`${host}/api/notes/deletenotes/${id}`, {
      method: "DELETE",
      headers: {
        "Content-Type": "application/json",
        "auth-token":
          "eyJhbGciOiJIUzI1NiIsInR5cCI6IkpXVCJ9.eyJ1c2VyIjp7ImlkIjoiNjRhOTQ4NGY1MmI5ZDVjZDRjYjlkNmVlIn0sImlhdCI6MTY4OTA2MTkyN30.coa6qVmo4RN2nIuDrSvjW_drJLqfF65XXzxENL4Rwe8",
      },
    });
    fetchAllNotes();
  };

  //EditNote
  const editNote = async (id, title, description, tag) => {
    //Changing in DB
    const body = JSON.stringify({ title, description, tag });
    await fetch(`${host}/api/notes/updatenotes/${id}`, {
      method: "PUT",
      headers: {
        "Content-Type": "application/json",
        "auth-token":
          "eyJhbGciOiJIUzI1NiIsInR5cCI6IkpXVCJ9.eyJ1c2VyIjp7ImlkIjoiNjRhOTQ4NGY1MmI5ZDVjZDRjYjlkNmVlIn0sImlhdCI6MTY4OTA2MTkyN30.coa6qVmo4RN2nIuDrSvjW_drJLqfF65XXzxENL4Rwe8",
      },
      body: body,
    });
    fetchAllNotes();
  };

  //Alert Functions
  const [alert, setAlert] = useState(null);
  const showAlert = (message, type)=>{
    setAlert({message, type});
    setTimeout(() => {
      setAlert(null);
    }, 1500);
  }


  return (
    <noteContext.Provider
      value={{
        notes,
        addNote,
        deleteNote,
        editNote,
        fetchAllNotes,
        DBerror,
        alert,
        showAlert
      }}
    >
      {props.children}
    </noteContext.Provider>
  );
};

export default NoteState;
