import React from "react";

const Alert = (props) => {
  // const [alert] = props;
  return (
    <div>
    { props.alert && <div className={`alert alert-${props.alert.type}`} role="alert">
      <b>{props.alert.message}</b>
    </div>}
    </div>
  );
};

export default Alert;
