import React, { useState } from "react";
import { Link, useLocation, useNavigate } from "react-router-dom";

export default function Navbar() {
  const [session,setSession] = useState(sessionStorage.getItem("iNotebookAuthToken"));
  const navigate = useNavigate();
  const location = useLocation();
  const handleLogout = ()=>{
    sessionStorage.removeItem("iNotebookAuthToken");
    setSession(null);
    navigate("/");
  }

  return (
    <nav className="navbar navbar-expand-lg navbar-dark bg-dark">
      <div className="container-fluid">
        <Link className="navbar-brand" to="/">
          iNoteBook
        </Link>
        <button
          className="navbar-toggler"
          type="button"
          data-bs-toggle="collapse"
          data-bs-target="#navbarSupportedContent"
          aria-controls="navbarSupportedContent"
          aria-expanded="false"
          aria-label="Toggle navigation"
        >
          <span className="navbar-toggler-icon"></span>
        </button>
        <div className="collapse navbar-collapse" id="navbarSupportedContent">
          <ul className="navbar-nav me-auto mb-2 mb-lg-0">
            {/* <li className="nav-item">
              <Link
                className={`nav-link ${
                  location.pathname === "/" ? "active" : ""
                }`}
                aria-current="page"
                to="/"
              >
                Home
              </Link>
            </li> */}

            <li className="nav-item">
              <Link
                className={`nav-link ${
                  location.pathname === "/about" ? "active" : ""
                }`}
                to="/about"
              >
                About
              </Link>
            </li>
          </ul>
        </div>
        { !sessionStorage.getItem("iNotebookAuthToken") ? <div className="d-flex">
          <Link className="btn btn-primary mx-1" to="/login" role="button">
            Login
          </Link>
          <Link className="btn btn-primary mx-1" to="/signup" role="button">
            Signup
          </Link>
        </div> : <button onClick={handleLogout} className="btn btn-primary">Logout</button> }
      </div>
    </nav>
  );
}
