import noteContext from "./noteContext";
import { useState } from "react";

const NoteState = (props) => {
  const host = "http://127.0.0.1:5000";

  // Getting All Notes
  const [notes, setNotes] = useState([]);
  const fetchAllNotes = async () => {
    const response = await fetch(`${host}/api/notes/fetchallnotes`, {
      method: "GET",
      headers: {
        "Content-Type": "application/json",
        "auth-token":
          "eyJhbGciOiJIUzI1NiIsInR5cCI6IkpXVCJ9.eyJ1c2VyIjp7ImlkIjoiNjRhOTQ4NGY1MmI5ZDVjZDRjYjlkNmVlIn0sImlhdCI6MTY4OTA2MTkyN30.coa6qVmo4RN2nIuDrSvjW_drJLqfF65XXzxENL4Rwe8",
      },
    });
    const allNotes = await response.json();
    setNotes(allNotes);
  };

  //AddNote
  const addNote = async (title, description, tag) => {
    //adding notes to DB
    const response = await fetch(`${host}/api/notes/addnotes`, {
      method: "POST",
      headers: {
        "Content-Type": "application/json",
        "auth-token":
          "eyJhbGciOiJIUzI1NiIsInR5cCI6IkpXVCJ9.eyJ1c2VyIjp7ImlkIjoiNjRhOTQ4NGY1MmI5ZDVjZDRjYjlkNmVlIn0sImlhdCI6MTY4OTA2MTkyN30.coa6qVmo4RN2nIuDrSvjW_drJLqfF65XXzxENL4Rwe8",
      },
      body: JSON.stringify({title, description, tag})
    });
    //adding notes to frontend
    const parsedResponse = await response.json();
    setNotes(notes.concat(parsedResponse));
    
  };

  //DeleteNote
  const deleteNote = async (id) => {
      await fetch(`${host}/api/notes/deletenotes/${id}`, {
      method: "DELETE",
      headers: {
        "Content-Type": "application/json",
        "auth-token":
          "eyJhbGciOiJIUzI1NiIsInR5cCI6IkpXVCJ9.eyJ1c2VyIjp7ImlkIjoiNjRhOTQ4NGY1MmI5ZDVjZDRjYjlkNmVlIn0sImlhdCI6MTY4OTA2MTkyN30.coa6qVmo4RN2nIuDrSvjW_drJLqfF65XXzxENL4Rwe8",
      },
    });
    // just to delete note from the front end
    const newNotes = notes.filter((note) => {
      return note._id !== id;
    });
    setNotes(newNotes);
  };

  //EditNote
  const editNote = async (id, title, description, tag) => {
    //Changing in DB
    let response = await fetch(`${host}/api/notes/updatenotes/${id}`,{
      method: "PUT",
      headers: {
        "Content-Type": "application-json",
        "auth-token":
          "eyJhbGciOiJIUzI1NiIsInR5cCI6IkpXVCJ9.eyJ1c2VyIjp7ImlkIjoiNjRhOTQ4NGY1MmI5ZDVjZDRjYjlkNmVlIn0sImlhdCI6MTY4OTA2MTkyN30.coa6qVmo4RN2nIuDrSvjW_drJLqfF65XXzxENL4Rwe8"
      },
      body: JSON.stringify({title, description, tag})
    })
    let parsedResponse = await response.json();
    console.log(parsedResponse);
    //Changing on frontend
    const newNotes = JSON.parse(JSON.stringify(notes));
    for(let i=0;i<newNotes.length;i++)
    {
      if(newNotes[i]._id === id)
      {
        newNotes[i].title = title;
        newNotes[i].description = description;
        newNotes[i].tag = tag;
        break;
      }
    }
    setNotes(newNotes);
  };

  return (
    <noteContext.Provider
      value={{ notes, addNote, deleteNote, editNote, fetchAllNotes }}
    >
      {props.children}
    </noteContext.Provider>
  );
};

export default NoteState;
