import React, { useState, useContext } from "react";
import { useNavigate } from "react-router-dom";
import noteContext from "../context/notes/noteContext";

const Login = () => {
  const context = useContext(noteContext);
  const {showAlert} = context;
  const navigate = useNavigate();
  const [credentials, setCredentials] = useState({ email: "", password: "" });
  const handleClick = async (e) => {
    e.preventDefault();
    const response = await fetch("http://127.0.0.1:5000/api/auth/login",{
      method: "POST",
      headers: {
        'Content-Type': 'application/json'
      },
      body: JSON.stringify({email: credentials.email, password: credentials.password})      
    });
    const parsedResponse = await response.json();
    if(parsedResponse.success === true)
      {
        sessionStorage.setItem("iNotebookAuthToken",parsedResponse.authToken);
        showAlert("Signed In Succesfully","success");
        navigate("/home");
      }
      else
      {
        showAlert("Please enter correct credentials","danger");
      }
  };
  const onChange = (e) => {
    setCredentials({ ...credentials, [e.target.name]: e.target.value });
  };
  //Added by me to increase security
  useEffect(() => {
    sessionStorage.removeItem("iNotebookAuthToken");
    // eslint-disable-next-line
  }, []);

  return (
    <form onSubmit={handleClick}>
      
      <div className="mb-3">
        <h1 className="my-3 text-center">Please Login to see your notes</h1>
        <label htmlFor="exampleInputEmail1" className="form-label">
          Email address
        </label>
        <input
          type="email"
          className="form-control"
          id="email"
          name="email"
          value={credentials.email}
          onChange={onChange}
        />
      </div>
      <div className="mb-3">
        <label htmlFor="exampleInputPassword1" className="form-label">
          Password
        </label>
        <input
          type="password"
          className="form-control"
          id="password"
          name="password"
          value={credentials.password}
          onChange={onChange}
        />
      </div>
      <button type="submit" className="btn btn-primary">
        Submit
      </button>
    </form>
  );
};

export default Login;
