import noteContext from "./noteContext";
import { useState } from "react";

const NoteState = (props) => {
  const host = "http://127.0.0.1:5000";

  // Getting All Notes
  const [notes, setNotes] = useState([]);
  const fetchAllNotes = async () => {
    const response = fetch(`${host}/api/notes/fetchallnotes`, {
      method: "GET",
      headers: {
        'Content-Type': 'appliation/json',
        "auth-token":
          "eyJhbGciOiJIUzI1NiIsInR5cCI6IkpXVCJ9.eyJ1c2VyIjp7ImlkIjoiNjRhOTQ4NGY1MmI5ZDVjZDRjYjlkNmVlIn0sImlhdCI6MTY4OTA2MTkyN30.coa6qVmo4RN2nIuDrSvjW_drJLqfF65XXzxENL4Rwe8",
      },
    });
    const allNotes = await response.json();
    setNotes(allNotes);
  };

  //AddNote
  const addNote = (title, description, tag) => {
    let note = {
      _id: "64ad11a9ca854d6b333b1d1f2c241234",
      user: "64a9484f52b9d5cd4cb9d6ee",
      title: title,
      description: description,
      tag: tag,
      date: "2023-07-11T08:24:09.004Z",
      __v: 0,
    };
    const newnotes = notes.concat(note);
    setNotes(newnotes);
  };

  //DeleteNote
  const deleteNote = (id) => {
    const newNotes = notes.filter((note) => {
      return note._id !== id;
    });
    setNotes(newNotes);
  };

  //EditNote
  const editNote = ()=>{

  }

  return (
    <noteContext.Provider value={{ notes, addNote, deleteNote, editNote, fetchAllNotes }}>
      {props.children}
    </noteContext.Provider>
  );
};

export default NoteState;
