import React, { useState, useContext, useEffect } from "react";
import { useNavigate } from "react-router-dom";
import noteContext from "../context/notes/noteContext";

const Signup = (e) => {
  const context = useContext(noteContext);
  const {showAlert} = context;
  const navigate = useNavigate();
  const [credentials, setCredentials] = useState({name: "", email: "", password: "", cpassword: ""})
  const onChange = (e)=>{
    setCredentials({...credentials, [e.target.name]: e.target.value})
  }
  const onSubmit = async (e)=>{
    e.preventDefault();
    if(credentials.cpassword !== credentials.password)
    {
      showAlert("Password and Confirm Password are not matching!","danger");
    }
    else
    {
      const response = await fetch("http://127.0.0.1:5000/api/auth/createuser",{
        method: "POST",
        headers: {
          'Content-Type': 'application/json'
        },
        body: JSON.stringify({name: credentials.name, email: credentials.email, password: credentials.password})
      })
      const parsedResponse = await response.json();
      if(parsedResponse.success === true)
      {
        sessionStorage.setItem("iNotebookAuthToken",parsedResponse.authToken);
        showAlert("User Created Successfully!","success");
        navigate("/home");
      }
      else if(parsedResponse.success === false)
      {
        showAlert("User with this email already Exists!","Danger");
      }
    }
  }
  //Added by me to increase security
  useEffect(() => {
    sessionStorage.removeItem("iNotebookAuthToken");
    // eslint-disable-next-line
  }, []);
  return (
    <div className="container">
      <h1 className="my-3 text-center">Please Signup to start uploading your notes</h1>
      <form onSubmit={onSubmit}>
        <div className="mb-3">
          <label htmlFor="exampleInputEmail1" className="form-label">
            Name
          </label>
          <input
            type="text"
            className="form-control"
            id="name"
            name="name"
            required
            value={credentials.name}
            onChange={onChange}
          />
        </div>
        <div className="mb-3">
          <label htmlFor="exampleInputEmail1" className="form-label">
            Email Address
          </label>
          <input
            type="email"
            className="form-control"
            id="email"
            name="email"
            required
            value={credentials.email}
            onChange={onChange}
          />
        </div>
        <div className="mb-3">
          <label htmlFor="exampleInputPassword1" className="form-label">
            Password
          </label>
          <input
            type="password"
            className="form-control"
            id="password"
            name="password"
            required
            minLength={5}
            value={credentials.password}
            onChange={onChange}
          />
        </div>
        <div className="mb-3">
          <label htmlFor="exampleInputPassword1" className="form-label">
            Confirm Password
          </label>
          <input
            type="password"
            className="form-control"
            id="cpassword"
            name="cpassword"
            required
            minLength={5}
            value={credentials.cpassword}
            onChange={onChange}
          />
        </div>
        <button type="submit" className="btn btn-primary">
          Submit
        </button>
      </form>
    </div>
  );
};

export default Signup;
